import React, { useEffect, useMemo, useState } from 'react';
import {
  Alert,
  Image,
  StyleSheet,
  Text,
  Platform,
  View,
  Linking,
  TouchableOpacity,
} from 'react-native';
import { useTranslation } from 'react-i18next';
import _ from 'lodash';
import { useDispatch, useSelector } from '@/store';
import { DEVICE_LARGE } from '@/utils/deviceConstants';
import { fontSize } from '@/theme/fonts';
import {
  WHITE,
  DARKER_GREY,
  BLACK,
  BLUE,
  ORANGE,
  YELLOW,
} from '@/theme/colors';
import {
  updateLinkedContext,
  selectLinkedContext,
  createSelectLinkedSigsForApp,
} from '@/actions';

import { isVerified } from '@/utils/verifications';
import Check from '../Icons/Check';

/**
 * App Card in the Apps Screen
 * each App should have:
 * @prop name
 * @prop logo
 * @prop url
 */
const MAX_WAITING_SECONDS = 60;

const AppCard = (props: AppInfo) => {
  const {
    url,
    id,
    logo,
    name,
    unusedSponsorships,
    context,
    testing,
    verifications: appVerifications,
    usingBlindSig,
  } = props;
  const dispatch = useDispatch();
  const userVerifications = useSelector(
    (state: State) => state.user.verifications,
  );
  const [verifiedCount, setVerifiedCount] = useState(0);

  // Make sure each instance of AppCard has it's own selector. Otherwise they would
  // invalidate each others cache. See https://react-redux.js.org/next/api/hooks#using-memoizing-selectors
  const linkedContextSelector = useMemo(() => selectLinkedContext, []);
  const linkedContext = useSelector((state: State) =>
    linkedContextSelector(state, context),
  );

  const appColorTheme = useMemo(() => {
    switch (name) {
      case 'Gitcoin':
        return '#8F37FF';
      case '1Hive':
        return '#FFD037';
      case 'RabbitHole':
        return '#0FCE7C';
      case 'IDChain':
        return '#2C227E';
      case 'Rare Coin Claims':
        return '#1DA1F2';
      case 'MultiverseDAO':
        return '#F21DDD';
      case 'clr.fund':
        return '#261A4B';
      case 'Burn Signal':
        return '#ED411B';
      case 'The Ether':
        return '#7CE0D6';
      case 'Top-up Gifter':
        return '#F38621';
      case 'Discord Unique Bot':
        return '#9EA1E0';
      case 'Snapshot':
        return '#FFAF37';
      case '13Votes':
        return '#B337FF';
      case '$Bright':
        return '#FFC918';
      case 'Lumos':
        return '#FFD337';
      default:
        return '';
    }
  }, [name]);
  const selectLinkedSigs = useMemo(
    () => createSelectLinkedSigsForApp(id),
    [id],
  );
  const linkedSigs = useSelector(selectLinkedSigs);

  const { t } = useTranslation();

  // Get verifications user has for this app
  useEffect(() => {
    let count = 0;
    for (const verification of appVerifications) {
      const verified = isVerified(
        _.keyBy(userVerifications, (v) => v.name),
        verification,
      );
      if (verified) {
        // console.log(`${name}: verified for ${verification}`);
        count++;
      } else {
        // console.log(`${name}: not verified for ${verification}`);
      }
    }
    setVerifiedCount(count);
  }, [appVerifications, name, userVerifications]);

  const isLinked = usingBlindSig
    ? linkedSigs.length > 0
    : linkedContext && linkedContext.state === 'applied';
  const notSponsored = unusedSponsorships < 1 || !unusedSponsorships;

  useEffect(() => {
    let stale_check_timer;
    if (linkedContext && linkedContext.state === 'pending') {
      let checkTime =
        linkedContext.dateAdded +
        MAX_WAITING_SECONDS * 1000 +
        5000 -
        Date.now();

      if (checkTime < 0) {
        console.log(`Warning - checkTime in past: ${checkTime}`);
        checkTime = 1000; // check in 1 second
      }

      console.log(`Marking linkedContext as stale in ${checkTime}ms.`);

      stale_check_timer = setTimeout(() => {
        dispatch(
          updateLinkedContext({
            contextId: linkedContext.contextId,
            state: 'failed',
          }),
        );
        Alert.alert('Error', `Failed to link with ${linkedContext.context}`);
      }, checkTime);
    }
    return () => {
      clearTimeout(stale_check_timer);
    };
  }, [linkedContext, dispatch]);

  const openApp = () => {
    if (url) {
      Alert.alert(
        '',
        t('apps.alert.text.checkWebsite', { name }),
        [
          {
            text: t('apps.alert.button.visitWebsite'),
            onPress: () => {
              Linking.openURL(url);
            },
          },
          {
            text: t('common.alert.cancel'),
            style: 'cancel',
            onPress: () => null,
          },
        ],
        { cancelable: true },
      );
    } else {
      console.log(`No url set for app ${name}`);
    }
  };

  const VerifiedLabel = () => {
    if (verifiedCount === appVerifications.length) {
      return (
        <View style={styles.verifiedContainer}>
          <Text style={styles.verifiedLabel}>verified</Text>
        </View>
      );
    } else if (verifiedCount > 0) {
      // partly verified
      return (
        <View style={styles.partVerifiedContainer}>
          <Text style={styles.partVerifiedLabel}>
            partly verified ({`${verifiedCount}/${appVerifications.length}`})
          </Text>
        </View>
      );
    } else {
      // no verification
      return (
        <View style={styles.unverifiedContainer}>
          <Text style={styles.unverifiedLabel}>
            unverified ({`${verifiedCount}/${appVerifications.length}`})
          </Text>
        </View>
      );
    }
  };

  const SponsorshipLabel = () => {
    if (notSponsored) {
      return (
        <View style={styles.sponsorshipContainer}>
          <Text style={styles.sponsorshipLabel}>no sponsorships</Text>
        </View>
      );
    } else {
      return null;
    }
  };

  const Labels = () => {
    return (
      <View style={styles.labelsContainer}>
        <SponsorshipLabel />
        <VerifiedLabel />
      </View>
    );
  };

  const LinkedSticker = () => {
    if (usingBlindSig) {
      // show x/y linked sigs
      return linkedSigs.length > 0 ? (
        <View style={styles.linkedContainer} testID={`Linked_${id}`}>
          <View style={styles.linkedSticker}>
            <Check
              width={fontSize[11]}
              height={fontSize[11]}
              strokeWidth={3}
              color={WHITE}
            />
          </View>
          <Text
            style={styles.linkedText}
          >{`Linked (${linkedSigs.length}/${appVerifications.length})`}</Text>
        </View>
      ) : null;
    } else {
      return isLinked ? (
        <View style={styles.linkedContainer} testID={`Linked_${id}`}>
          <View style={styles.linkedSticker}>
            <Check
              width={fontSize[11]}
              height={fontSize[11]}
              strokeWidth={3}
              color={WHITE}
            />
          </View>
          <Text style={styles.linkedText}>Linked</Text>
        </View>
      ) : null;
    }
  };

  // If app is testing and user is not linked, do not display card
  if (testing && !isLinked) {
    return null;
  }

  return (
    <TouchableOpacity
      style={[
        styles.container,
        styles.shadow,
        { opacity: notSponsored ? 0.5 : 1 },
      ]}
      testID={`app-${id}`}
      onPress={openApp}
    >
      <View
        style={[styles.logoBackground, { backgroundColor: appColorTheme }]}
      />
      <View style={[styles.logoContainer, !notSponsored && styles.shadow]}>
        <Image
          source={{
            uri: logo !== '' ? logo : null,
          }}
          style={styles.logo}
        />
      </View>

      <Text style={styles.label}>{name}</Text>

      <View style={styles.divider}>
        <LinkedSticker />
      </View>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  container: {
    width: '45%',
    aspectRatio: 1,
    alignItems: 'center',
    borderRadius: 10,
    backgroundColor: WHITE,
    paddingTop: 20,
  },
  shadow: {
    ...Platform.select({
      android: { shadowColor: 'rgba(0,0,0,1)' },
      ios: { shadowColor: 'rgba(0,0,0,0.2)' },
    }),
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 1,
    shadowRadius: 4,
    elevation: 5,
  },
  logoBackground: {
    position: 'absolute',
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
    backgroundColor: 'red',
    height: 50,
    width: '100%',
  },
  logoContainer: {
    height: 60,
    aspectRatio: 1,
    borderRadius: 50,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'white',
  },
  logo: {
    height: 35,
    aspectRatio: 1,
    resizeMode: 'contain',
  },
  label: {
    marginTop: 10,
    fontFamily: 'Poppins-Medium',
    fontSize: 12,
  },
  divider: {
    flex: 1,
    flexDirection: 'row',
    borderTopWidth: 0.5,
    width: '100%',
    marginTop: 10,
    paddingHorizontal: 15,
    alignItems: 'center',
  },
  linkedContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  linkedSticker: {
    alignItems: 'center',
    justifyContent: 'center',
    borderWidth: 1,
    borderColor: BLUE,
    borderRadius: 50,
    backgroundColor: BLUE,
    width: fontSize[18],
    height: fontSize[18],
    marginRight: 10,
  },
  linkedText: {
    fontFamily: 'Poppins-Regular',
    color: BLUE,
    marginRight: DEVICE_LARGE ? 15 : 10,
    fontSize: fontSize[13],
  },
  partVerifiedContainer: {
    alignItems: 'center',
    justifyContent: 'center',
    borderWidth: 1,
    borderColor: YELLOW,
    borderRadius: 10,
  },
  verifiedContainer: {
    alignItems: 'center',
    justifyContent: 'center',
    borderWidth: 1,
    borderColor: ORANGE,
    borderRadius: 10,
  },
  verifiedLabel: {
