import { DEVICE_LARGE } from '../utils/deviceConstants';

export const TWENTY_TWO = DEVICE_LARGE ? 22 : 20;

export const SIXTY = DEVICE_LARGE ? 60 : 50;

export const TWENTY = DEVICE_LARGE ? 20 : 17.5;
